    private Color ranColor(){
        Color c = new Color();
        c.r = (float) Math.random();
        c.g = (float) Math.random();
        c.b = (float) Math.random();
        c.a = 1;
        return c;
    }

    private float updateX(float originX, float delta){
        int valX = MathUtils.randomBoolean() ? MathUtils.random(0,movePixel) : MathUtils.random(-movePixel,0);
        originX += valX * delta;
        return originX;
    }


    private float checkX(float originX){
        if(originX < 0 || originX >= 500){
            return MathUtils.random(0,500);
        }
        return originX;
    }


    private void drawRect(Color c, float originX, float originY, float width, float height){
        game.shapeRenderer.setColor(c);
        game.shapeRenderer.rect(originX,originY,width,height);
    }

    public void update(float delta){
        if(Gdx.input.isKeyJustPressed(Input.Keys.D))
            game.setScreen(new Screen1(game));
    }

    @Override
    public void render (float delta) {
        update(delta);
        TScreenUtils.clearScreen();

        for(int i = 0; i < arrOriginX.length ; i++){
            arrOriginX[i] = updateX(arrOriginX[i], delta);
            arrOriginX[i] = checkX(arrOriginX[i]);
        }

        for(int i = 0; i < arrOriginY.length ; i++){
            arrOriginY[i] = updateX(arrOriginY[i], delta);
            arrOriginY[i] = checkX(arrOriginY[i]);
        }

        game.shapeRenderer.begin(ShapeRenderer.ShapeType.Filled);
        for(int i = 0; i < arrOriginY.length; i++)
            drawRect(ranColor(),arrOriginX[i],arrOriginY[i],width,height);
        game.shapeRenderer.end();
    }



